fun main(args: Array<String>) {

    //[0][0]    [0][1]  [0][2]
    //[1][0]    [1][1]  [1][2]
    //[2][0]    [2][1]  [2][2]

    val row123: IntArray = intArrayOf(1, 2, 3)
    val row456: IntArray = intArrayOf(4, 5, 6)
    val row789: IntArray = intArrayOf(7, 8, 9)

    val dialPad: Array<IntArray> = arrayOf(row123, row456, row789)

    val numbersMowings = arrayOf(
            "",
            "",
            "",
            "",
            "")

    //Part 1
    var x = 1
    var y = 1

    print("Answer1 ")

    for (numberMowings in numbersMowings) {
        for (numberMowing in numberMowings) {

            when (numberMowing) {
                "L".toCharArray()[0] -> x--
                "U".toCharArray()[0] -> y--
                "R".toCharArray()[0] -> x++
                "D".toCharArray()[0] -> y++
            }

            if (x > 2) x = 2
            if (y > 2) y = 2
            if (x < 0) x = 0
            if (y < 0) y = 0
        }

        print(dialPad[y][x])
    }

    print("\n")

    //Part 2
    val inputss = arrayOf("",
            "",
            "",
            "",
            "")

    val row11111: IntArray = intArrayOf(-1, -1, 1, -1, -1)
    val row234: IntArray = intArrayOf(-1,  2, 3,  4, -1)
    val row56789: IntArray = intArrayOf( 5,  6, 7,  8,  9)
    val rowabc: IntArray = intArrayOf(-1, -2, -3, -4, -1) //A,B,C
    val rowd: IntArray = intArrayOf(-1, -1, -5, -1, -1) //D

    val dialPadV2: Array<IntArray> = arrayOf(row11111, row234, row56789, rowabc, rowd)

    x = 0
    y = 2

    print("Answer2 ")

    for (inputs in inputss) {
        for (input in inputs) {

            val prevX = x
            val prevY = y

            when (input) {
                "L".toCharArray()[0] -> x--
                "U".toCharArray()[0] -> y--
                "R".toCharArray()[0] -> x++
                "D".toCharArray()[0] -> y++
            }

            if (x > 4) x = 4
            if (y > 4) y = 4
            if (x < 0) x = 0
            if (y < 0) y = 0

            if(dialPadV2[y][x] == -1) {
                x = prevX
                y = prevY
            }
        }

        print(getNumberString(dialPadV2[y][x]))
    }
}

fun getNumberString(number : Int) : String {
    if(number == -2) {
        return "A"
    } else if(number == -3) {
        return "B"
    } else if(number == -4) {
        return "C"
    } else if(number == -5) {
        return "D"
    } else {
        return number.toString()
    }
}